import React from "react";
import styled from "styled-components";
import cookie from "react-cookies";
import { NavLink as Link } from "react-router-dom";
import logo from "../../../assets/images/youtube.png";
import hamburger from "../../../assets/images/hamburger.png";

const Container = styled.div`
  width: 100%;
  height: 56px;
  display: flex;
`;
const TitleBox = styled.div`
  width: 169px;
  display: flex;
  align-items: center;
`;
const TitleImage = styled.img`
  width: 97px;
  height: 24px;
  padding: 18px 14px 18px 16px;
`;
const TitleImage2 = styled.img`
  width: 24px;
  height: 24px;
  padding: 18px;
`;
const Menu = styled.div`
  display: flex;
`;
const MenuTitle = styled.div`
  width: 370px;
  height: 40px;
  margin: 36px 0 13px;
  padding: 2px 0 2px 16px;
  box-sizing: border-box;
  background-color: #0f1333;
  color: white;
  font-size: 24px;
  font-weight: 700;
`;
const MenuTab = styled.a`
  width: 346px;
  height: 31px;
  font-size: 22px;
  padding: 0 0 2px 24px;
  margin-bottom: 5px;
  border-bottom: 1px solid #0f1333;
  color: #0f1333;
`;
const LogoutButton = styled.button`
  width: 177px;
  height: 51px;
  margin: 23px 0 0 37px;
  cursor: pointer;
`;

function Nav() {
  const logout = () => {
    if (window.confirm("Would you like to Sign out?")) {
      cookie.remove("user_token");
      document.location.href = "/";
    }
  };

  return (
    <Container>
      <TitleBox>
        <TitleImage2 src={hamburger} />
        <Link to="/">
          <TitleImage src={logo} />
        </Link>
      </TitleBox>
      <Menu>
        <MenuTitle>DIMF 회원 계정 관리</MenuTitle>
        <MenuTab>
          <Link to="/loginhistory">로그인 이력</Link>
        </MenuTab>
      </Menu>
      <LogoutButton onClick={() => logout()} />
    </Container>
  );
}

export default Nav;
