import React from "react";
import styled from "styled-components";
import cookie from "react-cookies";
import { NavLink as Link } from "react-router-dom";
import logo from "../../assets/images/youtube.png";
import hamburger from "../../assets/images/hamburger.png";

const Container = styled.div`
  width: 240px;
  height: 100%;
  padding: 0 16px;
  position: absolute;
  left: 0;
  background-color: white;
`;
const TitleBox = styled.div`
  width: 240px;
  height: 56px;
  display: flex;
`;
const TitleImage = styled.img`
  width: 97px;
  height: 24px;
  padding: 18px 14px 18px 16px;
`;
const TitleImage2 = styled.img`
  width: 26px;
  height: 26px;
  padding: 18px 8px 18px 8px;
`;
const ContentBox = styled.div``;
const Content = styled.div`
  width: 240px;
  height: 40px;
`;
const ContentImg = styled.img``;
const ContentText = styled.p``;
const LogoImg = styled.img`
  width: ${({ size }) => (size === "small" ? "20px" : "24px")};
  height: ${({ size }) => (size === "short" ? "20px" : "24px")};
  padding: 8px;
  margin-right: 8px;
  cursor: pointer;
`;

function NavMainModal() {
  const logout = () => {
    if (window.confirm("Would you like to Sign out?")) {
      cookie.remove("user_token");
      document.location.href = "/";
    }
  };
  const ContentList = [
    { Img: "", text: "홈" },
    { Img: "", text: "탐색" },
    { Img: "", text: "Shorts" },
    { Img: "", text: "구독" },
    { Img: "", text: "Originals" },
    { Img: "", text: "YouTube Music" },
    { Img: "", text: "보관함" },
  ];
  const PersonalList = [
    { Img: "", text: "보관함" },
    { Img: "", text: "시청 기록" },
    { Img: "", text: "내 동영상" },
    { Img: "", text: "나중에 볼 동영상" },
    { Img: "", text: "오프라인 저장 동영상" },
    { Img: "", text: "YouTube Music" },
    { Img: "", text: "좋아요 표시한 동영상" },
  ];
  const MoreList = [
    { Img: "", text: "영화" },
    { Img: "", text: "게임" },
    { Img: "", text: "실시간" },
    { Img: "", text: "학습" },
    { Img: "", text: "스포츠" },
  ];
  const SettingList = [
    { Img: "", text: "설정" },
    { Img: "", text: "신고 기록" },
    { Img: "", text: "고객센터" },
    { Img: "", text: "의견 보내기" },
  ];
  return (
    <Container>
      <TitleBox>
        <TitleImage2 src={hamburger} />
        <Link to="/">
          <TitleImage src={logo} />
        </Link>
      </TitleBox>
      <ContentBox>
        {ContentList.map((c) => (
          <Content>
            <ContentImg />
            <ContentText>{c.text}</ContentText>
          </Content>
        ))}
      </ContentBox>
      <ContentBox>
        {PersonalList.map((p) => (
          <Content>
            <ContentImg />
            <ContentText>{p.text}</ContentText>
          </Content>
        ))}
      </ContentBox>
      <ContentBox>
        {SettingList.map((s) => (
          <Content>
            <ContentImg />
            <ContentText>{s.text}</ContentText>
          </Content>
        ))}
      </ContentBox>
      <ContentBox>
        {MoreList.map((m) => (
          <Content>
            <ContentImg />
            <ContentText>{m.text}</ContentText>
          </Content>
        ))}
      </ContentBox>
    </Container>
  );
}

export default NavMainModal;
